def show_variable_examples():
    """
    Mostra exemplos de variáveis em Python.
    """
    inteiro = 10
    decimal = 3.14
    texto = "Olá, programador de sucesso!"
    booleano = True

    print("Exemplo de inteiro:", inteiro)
    print("Exemplo de decimal:", decimal)
    print("Exemplo de texto:", texto)
    print("Exemplo de booleano:", booleano)
