targets.expr:
histogram_quantile(
  0.99,
  sum by (le)(
    rate(
      nginx_ingress_controller_upstream_server_response_latency_ms_bucket{
        controller_class=~"$controller_class",
        ingress=~"$ingress",
        resource_namespace=~"$namespace",
        controller_pod=~"$pod"
      }[$__interval]
    )
  )
)

histogram_quantile(
  0.95,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        controller_class=~"$controller_class",
        ingress=~"$ingress",
        resource_namespace=~"$namespace",
        controller_pod=~"$pod"
      }[$__interval]
    )
  )
)

